// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "../survey/Survey.sol";

contract SurveyFactory {
    address public immutable implementation;

    event SurveyCreated(address indexed creator, address surveyAddress);

    constructor(address _implementation) {
        implementation = _implementation;
    }

    function createSurvey(
        string memory _question,
        string[] memory _options,
        uint _startTime,
        uint _endTime
    ) external payable returns (address) {
        address payable clone = payable(Clones.clone(implementation));

        Survey(payable(clone)).initialize{value: msg.value}(
    msg.sender,
    _question,
    _options,
    _startTime,
    _endTime
);


        emit SurveyCreated(msg.sender, clone);
        return clone;
    }
}

